function showPhoto(c,b,a){if(c==""){document.getElementById("preview").innerHTML="no photoalbum";return}if(window.XMLHttpRequest){xmlhttp=new XMLHttpRequest()}else{xmlhttp=new ActiveXObject("Microsoft.XMLHTTP")}xmlhttp.onreadystatechange=function(){if(xmlhttp.readyState==4&&xmlhttp.status==200){document.getElementById("preview").innerHTML=xmlhttp.responseText}};xmlhttp.open("GET","/modules/getPhoto.php?albumId="+c+"&photoCount="+b+"&photoNo="+a,true);xmlhttp.send()};